import React from "react";
import { ListGroup, ListGroupItem } from "reactstrap";
import { FaTrash } from "react-icons/fa";
import './todo.css';

//redux
import { connect } from "react-redux";
import { removeTodo } from "../action/todo";

const Todo = ({ todos, markComplete }) => {
  return (
    <>
      <div className="todo-container">
        <li>Todayâ€™s Tasks</li>
        <button>Pending</button>
        <button>Abandon</button>
        <button>Compeleted</button>
        <button>Inprogress</button>
      </div>
      <ListGroup className="mt-5 mb-2">
      {todos.map((todo) => (
        <ListGroupItem key={todo.id}>
          {todo.title}
          <span className="float-right" onClick={() => markComplete(todo.id)}>
            <FaTrash />
          </span>
        </ListGroupItem>
      ))}
    </ListGroup>
    </>
  );
};

const mapStateToProps = (state) => ({
  todos: state.todos,
});

const mapDispatchToProps = (dispatch) => ({
  markComplete: (id) => {
    dispatch(removeTodo(id));
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(Todo);
